package co.ptpl;

public class DoWhileTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Do while example
		
		int   i = 1; // 1.  Initialization
		do
		{
			System.out.print( "  " + i );
			i++;	// 3.  Increment.
		}while ( i <= 5 ); // 2.  Test / Condition

	}

}
