package co.ptpl;

public class ForWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// for loop and while loop
		
		for (int i = 1; i <= 5; i++) {
            int j = i;

            while (j <= 5) {
                System.out.print(j);
                j++;
            }

            System.out.println(" ");
		}

	}

}
