package co.ptpl;

public class NestedFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// For loop pattern
		
		 for (int i = 0; i < 5; i++) {
	            for (int j = 0; j <= i; j++) {
	                System.out.print("* ");
	            }
	            System.out.println();
		 }

	}

}
